// 搜索功能，支持包含匹配和高亮显示
function searchData() {
    const input = document.getElementById("searchInput").value.trim();
    const resultsDiv = document.getElementById("results");
    resultsDiv.innerHTML = "";

    if (input === "") {
        alert("Please enter a search term.");
        return;
    }

    // 查找匹配项（包含匹配）
    const filteredData = filterDataByColumn(0, input);

    if (filteredData.length > 0) {
        // 标红用户输入的内容
        const highlightedData = highlightMatches(filteredData, input);
        displayResults(highlightedData, true);
    } else {
        resultsDiv.innerText = "没有找到相關字，請嘗試改換其異體字，如將“顏”改成“顔”，收字範圍僅限《廣韻》。";
    }
}

// 高级搜索功能，检查第15个值包含用户输入，并将相关列表高亮显示
function searchAdvanced() {
    const input = document.getElementById("searchInput").value.trim();
    const resultsDiv = document.getElementById("results");
    resultsDiv.innerHTML = "";

    if (input === "") {
        alert("Please enter a search term.");
        return;
    }

    const matchingBValues = new Set();
    for (const item of dataList) {
        if (item[14] && item[14].includes(input)) {
            matchingBValues.add(item[0]);
        }
    }

    if (matchingBValues.size > 0) {
        const finalResults = dataList.filter(item => matchingBValues.has(item[0]));
        if (finalResults.length > 0) {
            const highlightedData = highlightMatches(finalResults, input);
            displayResults(highlightedData, true);
        } else {
            resultsDiv.innerText = "没有找到相關字，請嘗試改換其異體字，如將“顏”改成“顔”，收字範圍僅限《廣韻》。";
        }
    } else {
        resultsDiv.innerText = "没有找到相關字，請嘗試改換其異體字，如將“顏”改成“顔”，收字範圍僅限《廣韻》。";
    }
}

// 通用的正则表达式搜索功能，可以指定列索引，并高亮显示匹配内容
function searchByRegexInColumn(columnIndex) {
    const input = document.getElementById("searchInput").value.trim();
    const resultsDiv = document.getElementById("results");
    resultsDiv.innerHTML = "";

    if (input === "") {
        alert("Please enter a search term.");
        return;
    }

    let regex;
    try {
        regex = new RegExp(input, 'gi');
    } catch (e) {
        alert("Invalid regular expression.");
        return;
    }

    // 直接过滤数据，无需深拷贝
    const filteredData = dataList.filter(item => regex.test(item[columnIndex]));

    if (filteredData.length > 0) {
        // 仅在高亮显示时进行浅拷贝
        const highlightedData = filteredData.map(row => {
            const newRow = row.slice(); // 浅拷贝当前行
            newRow[columnIndex] = row[columnIndex].replace(regex, match => `<span style="color: red;">${match}</span>`);
            return newRow;
        });

        displayResults(highlightedData, true);
    } else {
        resultsDiv.innerText = "没有找到相關字，請嘗試改換其異體字，如將“顏”改成“顔”，收字範圍僅限《廣韻》。";
    }
}

