// 通用的列过滤和搜索功能
function searchByColumn(columnIndex) {
    const input = document.getElementById("searchInput").value.trim();
    const resultsDiv = document.getElementById("results");
    resultsDiv.innerHTML = "";

    if (input === "") {
        alert("Please enter a search term.");
        return;
    }

    const filteredData = filterDataByColumn(columnIndex, input);

    if (filteredData.length > 0) {
        const highlightedData = highlightMatches(filteredData, input);
        displayResults(highlightedData, true);
    } else {
        resultsDiv.innerText = "没有找到相關字，請嘗試改換其異體字，如將“顏”改成“顔”，收字範圍僅限《廣韻》。";
    }
}

// 通用的过滤函数
function filterDataByColumn(columnIndex, searchValue) {
    return dataList
        .filter(item => item[columnIndex].includes(searchValue))
        .map(row => row.slice()); // 使用浅拷贝防止修改原始数据
}

// 将匹配的部分标红
function highlightMatches(data, input, isRegex = false) {
    const regex = new RegExp(input, isRegex ? 'gi' : 'g');
    return data.map(row => {
        return row.map(cell => {
            if (typeof cell === 'string') {
                return cell.replace(regex, match => `<span style="color: red;">${match}</span>`);
            }
            return cell;
        });
    });
}

// 显示结果的函数
function displayResults(data, isHighlighted = false) {
    const resultsDiv = document.getElementById("results");
    const table = document.createElement("table");
    const headerRow = document.createElement("tr");

    // 固定表头为数字 1 到 15
    const headers = ["聲符", "諧聲域", "音節類型", "二級聲符", "上古聲", "上古韻基", "參考上古音", "切拼", "聲母", "開合", "等位", "韻目", "四聲", "反切", "字", "布按","聲母系"];
    headers.forEach(header => {
        const th = document.createElement("th");
        th.innerText = header;
        headerRow.appendChild(th);
    });

    table.appendChild(headerRow);

    // 填充表格数据
    data.forEach(row => {
        const rowElement = document.createElement("tr");
        row.forEach(cell => {
            const td = document.createElement("td");
            if (isHighlighted && typeof cell === 'string') {
                td.innerHTML = cell; // 使用 innerHTML 以处理包含 HTML 标签的内容
            } else {
                td.innerText = cell;
            }
            rowElement.appendChild(td);
        });
        table.appendChild(rowElement);
    });

    resultsDiv.appendChild(table);
}
